import React, { useState, useEffect, useCallback } from "react";
import classes from "./Home.module.css";
import ItemsSummary from "../ItemsSlider/ItemsSummary";
import ItemsSlider from "../ItemsSlider/ItemsSlider";

const Home = (props) => {
  const [items, setInfo] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const apiKey = "e23e4a5910a7b243c6dbf4ce8e03b707";

  const fetchItemsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`https://fakestoreapi.com/products`);
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const datas = await response.json();

      const transformedItems = datas.results.map((data) => {
        return {
          id: data.id,
          title: data.title,
          price: data.price,
          description: data.description,
        };
      });
      setInfo(transformedItems);
      console.log(transformedItems);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchItemsHandler();
  }, [fetchItemsHandler]);

  let content = <p>Found no product.</p>;

  if (items.length > 0) {
    content = <ItemsSlider />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <main>
      <ItemsSummary />
      <ItemsSlider />
      <ItemsSlider />
      <ItemsSlider />
      <ItemsSlider />
    </main>
  );
};

export default Home;
