import React, { useState } from "react";
import classes from "./TopBar.module.css";
import SearchIcon from "../search/SearchIcon";
import CartItem from "../Cart/cartItem/CartItem";
import Item from "../ItemsSlider/Item/Item";
import SearchedItem from "./SearchedItem";

const TopBar = (props) => {
  const [showSearch, setShowSearch] = useState(false);
  const [searchValue, setSearchValue] = useState("");

  const toggleSearch = () => {
    setShowSearch(!showSearch);
  };

  const search = (e) => {
    setSearchValue(e.target.value);
  };

  console.log(props.items);

  let searchedItems = [];
  const sss = props.items[i].title.split("");
  const vvv = searchValue.split("");
  for (let i = 0; i < sss.length; i++) {
    for (let j = 0; j < vvv.length; j++) {}
    if (
      props.items[i].title
        .toLowerCase()
        .split("")
        .find((lettre) => lettre === searchValue)
        .join()
    ) {
      searchedItems.push(props.items[i]);
    }
  }

  console.log(searchedItems);

  return (
    <div className={classes.topBar}>
      <p>{props.items.length} products</p>
      <div className={classes.filtersOption}>
        <div className={classes.searchBar}>
          <input
            type="text"
            onChange={search}
            className={classes.searchInput}
            placeholder="Search"
            style={{
              width: showSearch ? "100%" : "0",
              padding: showSearch ? "10px 0px" : "10px 0px",
            }}
          />
        </div>
        <span className={classes.searchIcon} onClick={toggleSearch}>
          <SearchIcon />
        </span>
        <span onClick={props.toggleSideBar}>
          {props.showSideBar ? "hide" : "show"} filters
        </span>
        <span>sort by</span>
      </div>
      {showSearch && (
        <div className={classes.searchResult}>
          <ul>
            {searchedItems.map((item, i) => (
              <SearchedItem
                id={item.id}
                key={i}
                title={item.title}
                description={item.description}
                price={item.price}
                image={item.image}
                gender={item.gender}
              />
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TopBar;
