import React, { useContext } from "react";
import ItemForm from "./ItemForm";
import classes from "./Item.module.css";
import CartContext from "../../UI/store/Cart-Context";
import Card from "../../UI/Card/Card";
import image1 from "../../../assets/images/mw_global_karl_kani_moment_870x1110.avif";

const Item = React.forwardRef((props, ref) => {
  const Context = useContext(CartContext);

  const price = `$${props.price.toFixed(2)}`;

  const addAmountToCart = (amount) => {
    Context.addItemsToCart({
      id: props.id,
      name: props.name,
      description: props.description,
      price: props.price,
      amount: amount,
    });
  };

  return (
    <Card className={classes.item}>
      <img src={image1} alt="" ref={ref} />
      <div className={classes.info}>
        <div>
          <h3>{props.name}</h3>
          <div className={classes.description}>{props.description}</div>
          <div className={classes.price}>{price}</div>
        </div>
        <div>
          <ItemForm takeAmount={addAmountToCart} />
        </div>
      </div>
    </Card>
  );
});
export default Item;
