        {newArray.map((item, i) => (
          <Item
            id={item.id}
            key={i}
            title={item.title}
            description={item.description}
            price={item.price}
            image={item.image}
            gender={item.gender}
            // isProductPage={isProductPage}
          />
        ))}
      </div>
    </>
  );
};

export default Products;
