import React, { useReducer } from "react";
import CartContext from "./Cart-Context";
import product1 from "../../../assets/images/1.webp";
import product2 from "../../../assets/images/2.webp";
import product3 from "../../../assets/images/3.webp";
import product4 from "../../../assets/images/4.webp";
import product5 from "../../../assets/images/5.webp";
import product6 from "../../../assets/images/6.webp";

const initialItemsState = {
  items: [],
  totalAmount: 0,
};

const itemsReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.addedItems.price * action.addedItems.amount;
    const existingItemIndex = state.items.findIndex(
      (item) => item.id === action.addedItems.id
    );

    let updatedItems;
    if (state.items[existingItemIndex]) {
      const updatedItem = {
        ...state.items[existingItemIndex],
        amount:
          state.items[existingItemIndex].amount + action.addedItems.amount,
      };
      updatedItems = [...state.items];
      updatedItems[existingItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.addedItems);
    }

    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  if (action.type === "REMOVE") {
    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.id
    );
    const updatedTotalAmount =
      state.totalAmount - state.items[existingCartItemIndex].price;

    let updatedItems;
    if (state.items[existingCartItemIndex].amount === 1) {
      updatedItems = state.items.filter((item) => item.id !== action.id);
    } else {
      const updatedItem = {
        ...state.items[existingCartItemIndex],
        amount: state.items[existingCartItemIndex].amount - 1,
      };
      updatedItems = [...state.items];
      updatedItems[existingCartItemIndex] = updatedItem;
    }

    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  return initialItemsState;
};

const CartContextProvider = (props) => {
  const [itemsState, dispatch] = useReducer(itemsReducer, initialItemsState);

  const AddedItemsHandler = (addedItems) => {
    dispatch({ type: "ADD", addedItems: addedItems });
  };

  const removeItemsHandler = (id) => {
    dispatch({ type: "REMOVE", id: id });
  };

  const DUMMY_ITEMS = [
    {
      id: "m1",
      title: "Adidas",
      description: "Forward-looking silhouettes like the Ultraboost...",
      image: product1,
      price: 22.99,
    },
    {
      id: "m2",
      title: "Converse",
      description: "The enduring appeal of the Chuck Taylor...",
      image: product2,
      price: 16.5,
    },
    {
      id: "m3",
      title: "Reebok",
      description:
        "Founded in Britain, based in Boston, and bought by Germany's own Adidas...",
      image: product3,
      price: 12.99,
    },
    {
      id: "m5",
      title: "Vans",
      description: "Authentic in a way most sneakers can only pretend to be...",
      image: product4,
      price: 18.99,
    },
    {
      id: "m6",
      title: "Puma",
      description: "A formidable selection of retro-inflected lace-ups...",
      image: product5,
      price: 18.99,
    },
    {
      id: "m7",
      title: "Nike",
      description: "baddest name in the sneaker business...",
      image: product6,
      price: 18.99,
    },
  ];

  const Context = {
    addItemsToCart: AddedItemsHandler,
    removeItemsFromCart: removeItemsHandler,
    AddedCartItem: itemsState.items,
    cartTotalAmount: itemsState.totalAmount,
  };

  //////////////////////////////////////////

  return (
    <CartContext.Provider value={Context}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartContextProvider;
