import React, { useState } from "react";
import classes from "./TopBar.module.css";
import SearchIcon from "../search/SearchIcon";
import CartItem from "../Cart/cartItem/CartItem";
import Item from "../ItemsSlider/Item/Item";
import SearchedItem from "./SearchedItem";

const TopBar = (props) => {
  const [showSearch, setShowSearch] = useState(true);
  const [searchValue, setSearchValue] = useState("");

  // const toggleSearch = () => {
  //   setShowSearch(!showSearch);
  // };

  const search = (e) => {
    setSearchValue(e.target.value);
  };

  console.log(props.items);

  let searchedItems = [];
  for (let i = 0; i < props.items.length; i++) {
    if (props.items[i].title.includes(searchValue) && searchValue !== "") {
      searchedItems.push(props.items[i]);
    }
  }

  console.log(searchedItems);

  return (
    <div className={classes.topBar}>
      <p>{props.items.length} products</p>
      <div className={classes.filtersOption}>
        <input
          type="text"
          onChange={search}
          className={classes.searchInput}
          placeholder="Search"
        />
        <span className={classes.searchIcon}>
          <SearchIcon />
        </span>
        <span onClick={props.toggleSideBar}>hide filters</span>
        <span>sort by</span>
      </div>
      {showSearch && (
        <div className={classes.searchResult}>
          <ul>
            {searchedItems.map((item, i) => (
              <SearchedItem
                id={item.id}
                key={i}
                title={item.title}
                description={item.description}
                price={item.price}
                image={item.image}
                gender={item.gender}
              />
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TopBar;
