import React, { useState } from "react";
import classes from "./TopBar.module.css";
import SearchIcon from "../search/SearchIcon";
import CartItem from "../Cart/cartItem/CartItem";
import Item from "../ItemsSlider/Item/Item";

const TopBar = (props) => {
  const [showSearch, setShowSearch] = useState(false);
  const [searchValue, setSearchValue] = useState("");

  const toggleSearch = () => {
    setShowSearch(!showSearch);
  };

  const search = (e) => {
    setSearchValue(e.target.value);
  };

  const searchedItems = props.items.find((item) => item.title === searchValue);

  return (
    <div className={classes.topBar}>
      <p>{props.items.length} products</p>
      <div className={classes.filtersOption}>
        <input type="text" onChange={search} />
        <span className={classes.searchIcon} onClick={toggleSearch}>
          <SearchIcon />
        </span>
        <span onClick={props.toggleSideBar}>hide filters</span>
        <span>sort by</span>
      </div>
      {showSearch && (
        <div className={classes.searchResult}>
          <ul>
            <li className={classes["cart-item"]}>
              <div className={classes["item-image"]}>
                <img src="img" alt="" />
              </div>
              <h3>title</h3>
              <div className={classes.summary}>
                <span className={classes.price}>itemprice</span>
              </div>
            </li>
            <li className={classes["cart-item"]}>
              <div className={classes["item-image"]}>
                <img src="img" alt="" />
              </div>
              <h3>title</h3>
              <div className={classes.summary}>
                <span className={classes.price}>itemprice</span>
              </div>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default TopBar;
