  //     if (!response.ok) {
  //       throw new Error("Something went wrong!");
  //     }

  //     const datas = await response.json();
  //     console.log(datas);
  //     const transformedItems = datas.map((data) => {
  //       return {
  //         id: data.id,
  //         title: data.title,
  //         price: data.price,
  //         description: data.description,
  //         image: data.image,
  //       };
  //     });
  //     setItems(transformedItems);
  //     console.log(transformedItems);
  //   } catch (error) {
  //     setError(error.message);
  //   }
  //   setIsLoading(false);
  // }, []);

  // useEffect(() => {
  //   fetchItemsHandler();
  // }, [fetchItemsHandler]);

  let content = <p>Found no product.</p>;

  if (items.length > 0) {
    content = <ItemsSlider />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <main>
      <div className={classes["main-banner"]}>
        <div className={classes.overlay}>
          <div className={classes.lead}>
            <h1>WORK UP A SWEAT</h1>
            <p>GEAR FOR GETTING OUTSIDE</p>
          </div>
        </div>
        <img src={backgrounds[index]} alt="" />
      </div>
      <ItemsSlider topSlider={isTop} />
      <Banner />
      <Categories onCategorieClick={props.onCategorieClick} />
      <ItemsSlider items={items} />
    </main>
  );
};

export default Home;
