import React, { useState } from "react";
import classes from "./ProductsPage.module.css";
import Item from "../ItemsSlider/Item/Item";
import Filter from "./filter/Filter";
import useFetch from "../../hooks/use-fecth";
import Notification from "../UI/Notifications/Notifications";

const ProductsPage = () => {
  const [showSideBar, setShowSideBar] = useState(true);
  const isProductPage = true;
  const [filterValue, setFilterValue] = useState("");
  const [checkBoxState, setCheckBoxState] = useState();

  const { items, error, isLoading } = useFetch(
    "http://localhost:3005/products"
  );

  let shownItems = [];
  if (checkBoxState) {
    const filteredItems = items.filter((data) => data.gender === filterValue);
    shownItems.unshift(...shownItems, ...filteredItems);
  } else {
    shownItems = items;
  }
  const sss = filterValue === "" ? items : shownItems;

  const hideSideBar = () => {
    setShowSideBar(!showSideBar);
  };

  const onCheckBox = (data) => {
    setFilterValue(data);
  };

  const onCheckBoxState = (value) => {
    setCheckBoxState(value);
  };

  return (
    <>
      <section className={classes.main}>
        <div className={classes.topBar}>
          <p>{items.length} products</p>
          <div className={classes.filtersOption}>
            <span onClick={hideSideBar}>hide filters</span>
            <span>sort by</span>
          </div>
        </div>

        <div
          className={classes.sideBar}
          style={{
            transform: showSideBar ? "translateX(0)" : "translateX(-100%)",
          }}
        >
          <Filter
            labels={["men", "women", "kids"]}
            onPassData={onCheckBox}
            passCheckBoxState={onCheckBoxState}
          />
          {/* <Filter labels={["$25 - $50", "$50 - $100", "$100 - $150"]} />
          <Filter labels={["adidas", "nike", "converse", "Reebok"]} />
          <Filter labels={["in stock", "out of stock"]} /> */}
        </div>

        <div
          className={classes.products}
          style={{ width: showSideBar ? "83%" : "100%" }}
        >
          {sss.map((item, i) => (
            <Item
              id={item.id}
              key={i}
              title={item.title}
              description={item.description}
              price={item.price}
              image={item.image}
              gender={item.gender}
              isProductPage={isProductPage}
            />
          ))}
        </div>
      </section>
    </>
  );
};

export default ProductsPage;
