import { useState, useEffect, useCallback } from "react";

const useFetch = (url, filter) => {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchItemsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const datas = await response.json();
      console.log(datas);
      if (filter) {
        const filteredItems = datas.filter((data) => {
          return data.gender === filter;
          // id: data.id,
          // title: data.title,
          // price: data.price,
          // description: data.description,
          // image: data.image,
        });
        console.log(filteredItems[0]);
        // setTransformedItems((transformedItems) => {
        //   return [filteredItems, ...transformedItems];
        // });
        setItems((items) => {
          return [filteredItems, ...items];
        });
      } else {
        const transformedItems = datas.map((data) => {
          return {
            id: data.id,
            title: data.title,
            price: data.price,
            description: data.description,
            image: data.image,
            gender: data.gender,
          };
        });
        setItems(transformedItems);
      }
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, [url, filter]);

  useEffect(() => {
    fetchItemsHandler();
  }, [fetchItemsHandler]);

  return { items, error, isLoading };
};

export default useFetch;
