import React, { useContext, useState, useRef, useEffect } from "react";
import classes from "./Products.module.css";
import Item from "../ItemsSlider/Item/Item";
import ItemsSlider from "../ItemsSlider/ItemsSlider";
import CartContext from "../UI/store/Cart-Context";
import Filter from "./filter/Filter";
import useFetch from "../../hooks/use-fecth";

// const DUMMY_DATA = [
//   {
//     id: "m1",
//     title: "Adidas",
//     description: "Forward-looking silhouettes like the Ultraboost...",
//     price: 22.99,
//   },
//   {
//     id: "m2",
//     title: "Converse",
//     description: "The enduring appeal of the Chuck Taylor...",
//     price: 16.5,
//   },
//   {
//     id: "m3",
//     title: "Reebok",
//     description:
//       "Founded in Britain, based in Boston, and bought by Germany's own Adidas...",
//     price: 12.99,
//   },
//   {
//     id: "m5",
//     title: "Vans",
//     description: "Authentic in a way most sneakers can only pretend to be...",
//     price: 18.99,
//   },
//   {
//     id: "m6",
//     title: "Puma",
//     description: "A formidable selection of retro-inflected lace-ups...",
//     price: 18.99,
//   },
//   {
//     id: "m7",
//     title: "Nike",
//     description: "baddest name in the sneaker business...",
//     price: 18.99,
//   },
// ];

const ProductsPage = () => {
  const { items, error, isLoading } = useFetch(
    "http://localhost:3005/products"
  );
  const [showSideBar, setShowSideBar] = useState(true);
  // const prodcutsRef = useRef("");

  const hideSideBar = () => {
    setShowSideBar(!showSideBar);
  };

  // useEffect(() => {
  //   showSideBar && (prodcutsRef.current.style.paddingLeft = "20%");
  //   !showSideBar && (prodcutsRef.current.style.paddingLeft = "0");
  // });

  return (
    <>
      <section className={classes.main}>
        <div className={classes.topBar}>
          <p>Serena Williams Design Crew(17)</p>
          <div className={classes.filtersOption}>
            <span onClick={hideSideBar}>hide filters</span>
            <span>sort by</span>
          </div>
        </div>

        <div
          className={classes.sideBar}
          style={{
            transform: showSideBar ? "translateX(0)" : "translateX(-100%)",
          }}
        >
          <Filter />
          <div className={classes.filter}>
            <div className={classes.filterChoice}>
              <p className={classes.title}>shop by price</p>

              <label className={classes.container} htmlFor="low">
                <input type="checkbox" id="low"></input>
                <span className={classes.checkmark}></span>
                <span>$25 - $50</span>
              </label>

              <label className={classes.container} htmlFor="medium">
                <input type="checkbox" id="medium"></input>
                <span className={classes.checkmark}></span>
                <span>$50 - $100</span>
              </label>

              <label className={classes.container} htmlFor="expensive">
                <input type="checkbox" id="expensive"></input>
                <span className={classes.checkmark}></span>
                <span>$100 - $150</span>
              </label>
            </div>
          </div>

          <div className={classes.filter}>
            <div className={classes.filterChoice}>
              <p className={classes.title}>brand</p>

              <label className={classes.container} htmlFor="adidas">
                <input type="checkbox" id="adidas"></input>
                <span className={classes.checkmark}></span>
                <span>adidas</span>
              </label>

              <label className={classes.container} htmlFor="nike">
                <input type="checkbox" id="nike"></input>
                <span className={classes.checkmark}></span>
                <span>nike</span>
              </label>

              <label className={classes.container} htmlFor="converse">
                <input type="checkbox" id="converse"></input>
                <span className={classes.checkmark}></span>
                <span>converse</span>
              </label>

              <label className={classes.container} htmlFor="Reebok">
                <input type="checkbox" id="Reebok"></input>
                <span className={classes.checkmark}></span>
                <span>Reebok</span>
              </label>
            </div>
          </div>

          <div className={classes.filter}>
            <div className={classes.filterChoice}>
              <p className={classes.title}>availability</p>

              <label className={classes.container} htmlFor="in stock">
                <input type="checkbox" id="in stock"></input>
                <span className={classes.checkmark}></span>
                <span>in stock</span>
              </label>

              <label className={classes.container} htmlFor="out of stock">
                <input type="checkbox" id="out of stock"></input>
                <span className={classes.checkmark}></span>
                <span>out of stock</span>
              </label>
            </div>
          </div>
        </div>

        <div
          className={classes.products}
          style={{ width: showSideBar ? "83%" : "100%" }}
        >
          {items.map((item, i) => (
            <Item
              id={item.id}
              key={item.id}
              title={item.title}
              description={item.description}
              price={item.price}
              image={item.image}
            />
          ))}
        </div>
      </section>
    </>
  );
};

export default ProductsPage;
