import React, { useReducer } from "react";
import CartContext from "./Cart-Context";

const initialMealsState = {
  meals: [],
  totalAmount: 0,
};

const mealsReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.addedMeals.price * action.addedMeals.amount;
    const existingMealIndex = state.meals.findIndex(
      (meal) => meal.id === action.addedMeals.id
    );

    let updatedItems;
    if (state.meals[existingMealIndex]) {
      const updatedItem = {
        ...state.meals[existingMealIndex],
        amount:
          state.meals[existingMealIndex].amount + action.addedMeals.amount,
      };
      updatedItems = [...state.meals];
      updatedItems[existingMealIndex] = updatedItem;
    } else {
      updatedItems = state.meals.concat(action.addedMeals);
    }

    return {
      meals: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  if (action.type === "REMOVE") {
    const existingCartItemIndex = state.meals.findIndex(
      (meal) => meal.id === action.id
    );
    const updatedTotalAmount =
      state.totalAmount - state.meals[existingCartItemIndex].price;

    let updatedItems;
    if (state.meals[existingCartItemIndex].amount === 1) {
      updatedItems = state.meals.filter((meal) => meal.id !== action.id);
    } else {
      const updatedItem = {
        ...state.meals[existingCartItemIndex],
        amount: state.meals[existingCartItemIndex].amount - 1,
      };
      updatedItems = [...state.meals];
      updatedItems[existingCartItemIndex] = updatedItem;
    }

    return {
      meals: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }
  return initialMealsState;
};

const CartContextProvider = (props) => {
  const [mealsState, dispatch] = useReducer(mealsReducer, initialMealsState);

  const AddedMealsHandler = (addedMeals) => {
    dispatch({ type: "ADD", addedMeals: addedMeals });
  };

  const removeMealHandler = (id) => {
    dispatch({ type: "REMOVE", id: id });
  };

  const Context = {
    addItemsToCart: AddedMealsHandler,
    removeItemsFromCart: removeMealHandler,
    AddedCartItem: mealsState.meals,
    cartTotalAmount: mealsState.totalAmount,
  };

  return (
    <CartContext.Provider value={Context}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartContextProvider;
