import { useState, useRef } from "react";
import Item from "./Item/Item";
import classes from "./ItemsSlider.module.css";
import { flushSync } from "react-dom";

const DUMMY_ITEMS = [
  {
    id: "m1",
    name: "Adidas",
    description: "forward-looking silhouettes like the Ultraboost...",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Converse",
    description: " the enduring appeal of the Chuck Taylor...",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Reebok",
    description:
      "Founded in Britain, based in Boston, and bought by Germany's own Adidas...",
    price: 12.99,
  },
  {
    id: "m5",
    name: "Vans",
    description: " authentic in a way most sneakers can only pretend to be...",
    price: 18.99,
  },
  {
    id: "m6",
    name: "Puma",
    description:
      "A sneaker powerhouse with a formidable selection of retro-inflected lace-ups...",
    price: 18.99,
  },
  {
    id: "m7",
    name: "Nike",
    description: "baddest name in the sneaker business...",
    price: 18.99,
  },
  {
    id: "m8",
    name: "New Balance",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m9",
    name: "Jordan Brand",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m10",
    name: "Yeezy",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m11",
    name: "Saucony",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m12",
    name: "Veja",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m13",
    name: "Salomon",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableItems = (props) => {
  const selectedRef = useRef();
  const [index, setIndex] = useState(0);
  const onClickHandle = (direction) => {
    flushSync(() => {
      if (direction === "right") {
        if (index <= DUMMY_ITEMS.length - 1) {
          setIndex(index + 4);
        } else {
          setIndex(0);
        }
      } else {
        if (index > 0) {
          setIndex(index - 4);
        } else {
          setIndex(0);
        }
      }
      console.log(index);
    });
    selectedRef.current.scrollIntoView({
      behavior: "smooth",
      block: "nearest",
      inline: "start",
    });
  };
  return (
    <section className={classes.row}>
      <button
        className={`${classes.handle} ${classes["left-handle"]}`}
        onClick={() => onClickHandle("left")}
      >
        <div className={classes.text}>&#8249;</div>
      </button>
      <div className={classes.slider}>
        {DUMMY_ITEMS.map((item, i) => (
          <Item
            id={item.id}
            key={item.id}
            name={item.name}
            description={item.description}
            price={item.price}
            ref={index === i ? selectedRef : null}
          />
        ))}
      </div>

      <button
        className={`${classes.handle} ${classes["right-handle"]}`}
        onClick={() => onClickHandle("right")}
        // onClick={() => scroll("right")}
      >
        <div className={classes.text}>&#8250;</div>
      </button>
    </section>
  );
};

export default AvailableItems;
