  // console.log(users);

  const filter = {
    gender: ["Women", "Kids"],
    price: ["$25 - $50", "$50 - $100", "$100 - $150"],
    brand: ["adidas", "nike", "converse", "Reebok"],
    stock: ["in stock", "out of stock"],
  };
  // let users = [
  //   {
  //     name: "John",
  //     email: "johnson@mail.com",
  //     age: 25,
  //     address: "USA",
  //   },
