  //     age: 28,
  //     address: "England",
  //   },
  // ];

  console.log(filter.gender[1]);

  const newFilter = props.items.filter((item) => {
    for (let i = 0; i < props.items; i++) {
      for (let key in filter) {
        if (item[key][i] === undefined || item[key] != filter[key])
          return false;
      }
    }
    // return true;
  });

  console.log(newFilter);

  return (
    <>
      <div
        className={classes.products}
        style={{ width: props.onShowSideBar ? "83%" : "100%" }}
      >
        {props.items.map((item, i) => (
          <Item
            id={item.id}
            key={i}
            title={item.title}
            description={item.description}
            price={item.price}
            image={item.image}
            gender={item.gender}
            // isProductPage={isProductPage}
          />
        ))}
      </div>
    </>
  );
};

export default Products;
