import { useContext, useState } from "react";
import classes from "./Cart.module.css";
import CartItem from "./cartItem/CartItem";
import Modal from "../UI/Modal";
import CartContext from "../UI/store/Cart-Context";
import CartForm from "./cartForm/CartForm";

const Cart = (props) => {
  const Context = useContext(CartContext);
  const cartItems = Context.AddedCartItem.length;
  const [showForm, setShowForm] = useState(false);

  const cartItemRemoveHandler = (id) => {
    Context.removeMealsFromCart(id);
  };

  const cartItemAddHandler = (item) => {
    Context.addMealsToCart({ ...item, amount: 1 });
  };

  const onOpenForm = () => {
    if (cartItems > 0) {
      setShowForm(true);
    }
  };

  const onCloseForm = () => {
    setShowForm(false);
  };

  return (
    <Modal hideCartFromBackdrop={props.onHideCart}>
      <ul className={classes["cart-items"]}>
        {Context.AddedCartItem.map((item) => (
          <CartItem
            key={item.id}
            id={item.id}
            name={item.name}
            amount={item.amount}
            price={item.price}
            onAdd={cartItemAddHandler.bind(null, item)}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
          />
        ))}
      </ul>
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{Context.cartTotalAmount.toFixed(2)}</span>
      </div>
      {showForm && <CartForm onCloseForm={showForm} />}
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onHideCart}>
          Close
        </button>
        <button className={classes.button} onClick={onOpenForm}>
          Order
        </button>
      </div>
    </Modal>
  );
};

export default Cart;
