import React, { useState, useEffect, useCallback } from "react";
import classes from "./Home.module.css";
import Banner from "../ItemsSlider/Banner";
import Categories from "../categories/Categories";
import ItemsSlider from "../ItemsSlider/ItemsSlider";

const Home = (props) => {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchItemsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        `https://asos2.p.rapidapi.com/products/v2/list`,
        {
          params: {
            limit: "48",
            categoryId: "4209",
            offset: "0",
            store: "US",
            lang: "en-US",
            sizeSchema: "US",
            currency: "USD",
            sort: "freshness",
            country: "US",
          },

          method: "GET",
          headers: {
            "X-RapidAPI-Key":
              "60c04c5f27msh3ba2a9f6a073424p150bf5jsn517b435134cc",
            "X-RapidAPI-Host": "asos2.p.rapidapi.com",
          },
        }
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const datas = await response.json();
      console.log(datas);
      const transformedItems = datas.map((data) => {
        return {
          id: data.id,
          title: data.title,
          price: data.price,
          description: data.description,
          image: data.image,
        };
      });
      setItems(transformedItems);
      console.log(transformedItems);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchItemsHandler();
  }, [fetchItemsHandler]);

  let content = <p>Found no product.</p>;

  if (items.length > 0) {
    content = <ItemsSlider />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <main>
      <Banner />
      <Categories />
      <ItemsSlider items={items} />
    </main>
  );
};

export default Home;
