  let content = <p>Found no product.</p>;

  if (props.items.length > 0) {
    content = <ItemsSlider />;
  }

  if (props.error) {
    content = <p>{props.error}</p>;
  }

  if (props.isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <>
      <div className={classes["main-banner"]}>
        <div className={classes.overlay}>
          <div className={classes.lead}>
            <h1>WORK UP A SWEAT</h1>
            <p>GEAR FOR GETTING OUTSIDE</p>
          </div>
        </div>
        <img src={backgrounds[index]} alt="" />
      </div>
      <ItemsSlider items={props.items} topSlider={isTop} />
      <Banner />
      <Categories />
      <ItemsSlider items={props.items} />
    </>
  );
};

export default HomePage;
