import React from "react";
import TopBar from "../components/products/TopBar";
import Products from "../components/products/Products";
import useFetch from "../hooks/use-fecth";
import { useState } from "react";
import SideBar from "../components/products/SideBar";

const ProductsPage = () => {
  const { items, error, isLoading } = useFetch(
    "http://localhost:3005/products"
  );
  const [showSideBar, setShowSideBar] = useState();

  const handleToggleSideBar = () => {
    setShowSideBar(!showSideBar);
  };

  return (
    <>
      <TopBar items={items} toggleSideBar={handleToggleSideBar} />
      <SideBar onShowSideBar={showSideBar} showSideBar={showSideBar} />
      <Products items={items} onShowSideBar={showSideBar} />
    </>
  );
};

export default ProductsPage;
