import { useState, useRef } from "react";
import Item from "./Item/Item";
import classes from "./ItemsSlider.module.css";
import { flushSync } from "react-dom";

const DUMMY_ITEMS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
  },
  {
    id: "m5",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m6",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m7",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m8",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m9",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m10",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m11",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
  {
    id: "m12",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableMeals = (props) => {
  const selectedRef = useRef();
  const [index, setIndex] = useState(0);
  const onClickHandle = (direction) => {
    flushSync(() => {
      if (direction === "right") {
        if (index < DUMMY_ITEMS.length - 1) {
          setIndex(index + 1);
        } else {
          setIndex(0);
        }
      } else {
        if (index > 0) {
          setIndex(index - 1);
        } else {
          setIndex(0);
        }
      }
      console.log(index);
    });
    selectedRef.current.scrollIntoView({
      behavior: "smooth",
      inline: "end",
    });
  };
  return (
    <section className={classes.row}>
      <button
        className={`${classes.handle} ${classes["left-handle"]}`}
        onClick={() => onClickHandle("left")}
      >
        <div className={classes.text}>&#8249;</div>
      </button>
      <div className={classes.slider}>
        {DUMMY_ITEMS.map((meal, i) => (
          <Item
            id={meal.id}
            key={meal.id}
            name={meal.name}
            description={meal.description}
            price={meal.price}
            ref={index === i ? selectedRef : null}
          />
        ))}
      </div>

      <button
        className={`${classes.handle} ${classes["right-handle"]}`}
        onClick={() => onClickHandle("right")}
        // onClick={() => scroll("right")}
      >
        <div className={classes.text}>&#8250;</div>
      </button>
    </section>
  );
};

export default AvailableMeals;
