import React, { useState, useEffect, useCallback } from "react";
import classes from "./Home.module.css";
import ItemsSummary from "../ItemsSlider/ItemsSummary";
import ItemsSlider from "../ItemsSlider/ItemsSlider";

const Home = (props) => {
  const [items, setInfo] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const apiKey = "e23e4a5910a7b243c6dbf4ce8e03b707";

  const fetchItemssHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        `https://api.themoviedb.org/3/tv/popular?api_key=${apiKey}&language=en-US&page=1`
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const datas = await response.json();

      const transformedItems = datas.results.map((data) => {
        return {
          // id: data.id,
          // title: data.original_title,
          // match: data.popularity,
          // releaseDate: data.release_date,
          // image: `${imagePath}${data.backdrop_path}`,
        };
      });
      setInfo(transformedItems);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchItemssHandler();
  }, [fetchItemssHandler]);

  let content = <p>Found no product.</p>;

  if (items.length > 0) {
    content = <Row passItems={items} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <main>
      <ItemsSummary />
      <ItemsSlider />
      <ItemsSlider />
      <ItemsSlider />
      <ItemsSlider />
    </main>
  );
};

export default Home;
