import React, { useState, useEffect } from "react";
import Login from "./components/Login/Login";
import Home from "./components/Home/Home";
import Header from "./components/Layout/Header";
import Cart from "./components/Cart/Cart";
import CartContextProvider from "./components/UI/store/CartContextProvider";
import "./App.css";

function App() {
  const cartRef = useRef();

  const [shown, setShowCartModal] = useState(false);
  const [loggedIn, setIsLoggedIn] = useState(false);
  useEffect(() => {
    if (localStorage.getItem("loggedIn") === "1") {
      setIsLoggedIn(true);
    }
  }, []);

  const showCartModalHndler = () => {
    setShowCartModal(true);
    document.querySelector("body").style.position = "fixed";
  };

  const hideCartModalHandler = () => {
    setShowCartModal(false);
  };

  const loginHandler = (email, password) => {
    if (email.includes("@") && password.trim().length > 5) {
      localStorage.setItem("loggedIn", "1");
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  };

  const logoutHandler = () => {
    localStorage.removeItem("loggedIn");
    setIsLoggedIn(false);
  };

  return (
    <CartContextProvider>
      {shown && <Cart onHideCart={hideCartModalHandler} ref={cartRef} />}
      <Header
        onShowCart={showCartModalHndler}
        onLogout={logoutHandler}
        isLoggedIn={loggedIn}
      />

      {!loggedIn && <Login onLogin={loginHandler} />}
      {loggedIn && <Home />}
    </CartContextProvider>
  );
}

export default App;
