  // }, [showNotif]);

  const hideNotifHandler = () => {
    showNotif = false;
  };
  console.log(showNotif);
  return (
    <header>
      <div className={classes.container}>
        <div className={classes.logo}>
          <h2>StoreName</h2>
        </div>
        {props.isLoggedIn && (
          <>
            <CategoriesBar />
            <Navigation
              showCart={props.onShowCart}
              onShownav={props.isLoggedIn}
              onLogoutNav={props.onLogout}
            />
          </>
        )}
      </div>
      {showNotif && (
        <Notification
          title={notifTitle}
          message={notifMessage}
          onHideNotif={hideNotifHandler}
        />
      )}
    </header>
  );
};

export default Header;
